import { Suspense } from "react";
import { Await, useLoaderData } from "react-router-dom";
import EventItem from "../components/EventItem";
import EventsList from "../components/EventsList";

export default function EventDetailsPage(){

    const {event, events} = useLoaderData();

    return <>
        <Suspense fallback={<main><p>Loading...</p></main>}>
            <Await resolve={event}>
                {(loadedEvent)=><EventItem event={loadedEvent}/>}
            </Await>
        </Suspense>
        <Suspense fallback={<main><p>Loading...</p></main>}>
            <Await resolve={events}>
                {(loadedEvents)=>{
                    const filteredEvents= loadedEvents.map((eventItem)=>{
                        return eventItem.id !== event.id;
                    });
                    return <EventsList events={filteredEvents}/>
                }}
            </Await>
        </Suspense>
    </>
}

const eventLoader= async(id)=>{
    const response= await fetch("http://localhost:8080/events/"+id);

    if(!response.ok){
        throw json({message: "Couldn't fetch event details!"},{status:500});
    }

    else{
        const resData = await response.json();
        return resData.event;
    }
};

const eventsLoader=async()=>{
    const response= await fetch("http://localhost:8080/events");

    if(!response.ok){
        throw json({message: "Couldn't fetch events!"},{status:500});
    }

    else{
        const resData = await response.json();
        return resData.events;
    }
}

export const loader=async({request, params})=>{
    const id= params.id;
    return defer({
        eventKey: await eventLoader(id),
        eventsKey: eventsLoader(),
    });
}
